import React, { useState } from 'react';
import validator from 'validator';
import { Form, Button, Container, Row, Col } from 'react-bootstrap';
import { useNavigate, Link } from 'react-router-dom';
import './LoginUser.css'; // Ensure this CSS file is imported

function LoginForm_A() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [validationMessage, setValidationMessage] = useState('');
  const [messageColor, setMessageColor] = useState('black');
  const navigate = useNavigate();

  const validatePassword = (value) => {
    if (
      validator.isStrongPassword(value, {
        minLength: 8,
        minLowercase: 1,
        minUppercase: 1,
        minNumbers: 1,
        minSymbols: 1,
      })
    ) {
      return true;
    }
    return false;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!validator.isEmail(email)) {
      setValidationMessage('Please enter a valid email address.');
      setMessageColor('red');
      return;
    }
    if (!validatePassword(password)) {
      setValidationMessage('Password must be at least 8 characters long and include uppercase, lowercase, number, and symbol.');
      setMessageColor('red');
      return;
    }
    // Proceed with login logic
    setValidationMessage('Login successful!');
    setMessageColor('green');
    navigate('/dashboard'); // Redirect to dashboard after successful login
  };

  return (
    <Container className="container">
      <Row className="justify-content-center">
        <Col xs={12} md={6}>
          <div className="card">
            <h2 className="text-center">Login</h2>
            <Form onSubmit={handleSubmit}>
              <Form.Group controlId="formEmail">
                <Form.Control
                  type="email"
                  placeholder="Email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  className="form-control"
                />
              </Form.Group>
              <Form.Group controlId="formPassword">
                <Form.Control
                  type="password"
                  placeholder="Password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  className="form-control"
                />
              </Form.Group>
              <Button type="submit" className="btn-primary">Login</Button>
              {validationMessage && (
                <div className="validation-message" style={{ color: messageColor }}>
                  {validationMessage}
                </div>
              )}
            </Form>
            <Link to="/register" className="text-decoration-none">Don't have an account? Register here</Link>
          </div>
        </Col>
      </Row>
    </Container>
  );
}

export default LoginForm_A;
