import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import "./BookingPage.css";

const BookingPage = () => {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [selectedTime, setSelectedTime] = useState({
    hour: "12",
    minute: "00",
    period: "AM",
  });
  const [formData, setFormData] = useState({
    email: "",
    name: "",
    phone: "",
    description: "",
  });
  const [errors, setErrors] = useState({});
  const [showPreview, setShowPreview] = useState(false); // State for modal visibility

  const [selectedLocation, setSelectedLocation] = useState("Pune");
  const [selectedService, setSelectedService] = useState("Cleaning");
  const [selectedWorker, setSelectedWorker] = useState("Helper 1");

  const validateForm = () => {
    let newErrors = {};

    if (!formData.email.match(/^[^\s@]+@[^\s@]+\.[^\s@]+$/)) {
      newErrors.email = "Invalid email format";
    }

    if (!formData.name.match(/^[A-Za-z ]+$/)) {
      newErrors.name = "Name should only contain letters and spaces";
    }

    if (!formData.phone.match(/^\d{10}$/)) {
      newErrors.phone = "Phone number must be exactly 10 digits";
    }

    if (formData.description.length > 200) {
      newErrors.description = "Description should not exceed 200 characters";
    }

    if (!selectedDate) {
      newErrors.date = "Please select a valid date";
    }

    if (!selectedTime.hour || !selectedTime.minute || !selectedTime.period) {
      newErrors.time = "Please select a valid time";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleFormChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleTimeChange = (e) => {
    const { name, value } = e.target;
    setSelectedTime({ ...selectedTime, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (validateForm()) {
      const bookingData = {
        selectedDate,
        selectedTime: `${selectedTime.hour}:${selectedTime.minute} ${selectedTime.period}`,
        formData,
        selectedLocation,
        selectedService,
        selectedWorker,
      };

      try {
        const response = await fetch("/api/bookings", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(bookingData),
        });

        if ( response.ok) {
          alert("Booking Submitted! The worker has been notified.");
        } else {
          alert("Failed to submit booking. Please try again.");
        }
      } catch (error) {
        console.error("Error submitting booking:", error);
        alert("An error occurred. Please try again later.");
      }
    }
  };

  return (
    <div className="booking-page">
      <div className="booking-container">
        <h2>Book an Appointment</h2>
        <div className="form-section">
          <div className="dropdowns">
            <div className="form-group">
              <label>Location</label>
              <select value={selectedLocation} onChange={(e) => setSelectedLocation(e.target.value)}>
                <option value="Pune">Pune</option>
                <option value="Mumbai">Mumbai</option>
                <option value="Nashik">Nashik</option>
                <option value="Nagpur">Nagpur</option>
              </select>
            </div>
            <div className="form-group">
              <label>Service</label>
              <select value={selectedService} onChange={(e) => setSelectedService(e.target.value)}>
                <option value="Cleaning">Cleaning</option>
                <option value="Cooking">Cooking</option>
                <option value="Gardening">Gardening</option>
                <option value="Babysitting">Babysitting</option>
                <option value="Patient Care">Patient Care</option>
                <option value="Pet Care">Pet Care</option>
              </select>
            </div>
            <div className="form-group">
              <label>Worker</label>
              <select value={selectedWorker} onChange={(e) => setSelectedWorker(e.target.value)}>
                <option value="Helper 1">Helper 1</option>
                <option value="Helper 2">Helper 2</option>
              </select>
            </div>
          </div>
          <div className="calendar-section">
            <div className="form-group">
              <label>Select Date</label>
              <DatePicker selected={selectedDate} onChange={(date) => setSelectedDate(date)} dateFormat="yyyy/MM/dd" />
            </div>
            <div className="form-group">
              <label>Select Time</label>
              <select name="hour" value={selectedTime.hour} onChange={handleTimeChange}>
                {[...Array(12)].map((_, i) => (
                  <option key={i} value={i + 1}>{i + 1}</option>
                ))}
              </select>
              <select name="minute" value={selectedTime.minute} onChange={handleTimeChange}>
                {[0, 15, 30, 45].map((min) => (
                  <option key={min} value={min}>{min < 10 ? `0${min}` : min}</option>
                ))}
              </select>
              <select name="period" value={selectedTime.period} onChange={handleTimeChange}>
                <option value="AM">AM</option>
                <option value="PM">PM</option>
              </select>
            </div>
          </div>
          <form className="personal-info" onSubmit={handleSubmit}>
            <h3>Personal Information</h3>
            <div className="form-group">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleFormChange}
                required
              />
              {errors.email && <p className="error">{errors.email}</p>}
            </div>
            <div className="form-group">
              <label>Name</label>
              <input
                type="text"
                name="name"
                value={formData.name}
                onChange={handleFormChange}
                required
              />
              {errors.name && <p className="error">{errors.name}</p>}
            </div>
            <div className="form-group">
              <label>Phone</label>
              <input
                type="tel"
                name="phone"
                value={formData.phone}
                onChange={handleFormChange}
                required
              />
              {errors.phone && <p className="error">{errors.phone}</p>}
            </div>
            <div className="form-group">
              <label>Description</label>
              <textarea
                name="description"
                value={formData.description}
                onChange={handleFormChange}
              />
              {errors.description && <p className="error">{errors.description}</p>}
            </div>
            <button type="submit" className="submit-btn">Submit Booking</button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default BookingPage;
